mapper.select:
select , tc.name company_name, tr.title recruitment_title from t_enrollment_approval tea left join t_enrollment te on te.id = tea.enrollment_id left join t_recruitment tr on tr.id = te.recruitment_id left join t_company tc on tc.id=tr.company_id
tea.data_state = 1
and te.recruitment_id = #{obj.recruitment.id}
and tr.company_id = #{obj.company.id}
tc.name like concat('%',#{obj.company.name},'%')

select conut(1) from t_enrollment_approval tea left join t_enrollment te on te.id = tea.enrollment_id left join t_recruitment tr on tr.id = te.recruitment_id left join t_company tc on tc.id=tr.company_id
tea.data_state = 1
and te.recruitment_id = #{obj.recruitmentId}
and tr.company_id = #{obj.companyId}

SELECT count(1) from t_enrollment_approval tea 
left join t_enrollment te on te.id = tea.enrollment_id
left join t_recruitment tr on tr.id = te.recruitment_id 
left join t_company tc on tc.id=tr.company_id
left join t_user tu on tu.id = tea.user_id

	tea.data_state = 1 
	
		and tea.type = #{obj.type}
	
	
		and tea.state = #{obj.state}
	
	
		and tu.name = #{obj.user.name}
	
	
		and tu.mobile = #{obj.user.mobile}
	
	
		and tc.name like concat('%',#{obj.company.name},'%')
	
	
		and tea.create_time >= #{obj.enrollTimeStart}
	
	
		and #{obj.enrollTimeEnd} >= tea.create_time

SELECT , tc.id company_id, tc.name company_name, tr.id recruitment_id, tr.title recruitment_title, tu.name as user_name, tu.mobile as user_mobile from t_enrollment_approval tea left join t_enrollment te on te.id = tea.enrollment_id left join t_recruitment tr on tr.id = te.recruitment_id left join t_company tc on tc.id=tr.company_id left join t_user tu on tu.id=tea.user_id
tea.data_state = 1
and tea.type = #{obj.type}
and tea.state = #{obj.state}
and tu.name = #{obj.user.name}
and tu.mobile = #{obj.user.mobile}
and tc.name like concat('%',#{obj.company.name},'%')
and tea.create_time >= #{obj.enrollTimeStart}
and #{obj.enrollTimeEnd} >= tea.create_time
order by tea.create_time asc

update.foreach:
update t_enrollment_approval

	data_state=#{item.dataState},
	update_time = #{item.updateTime}

where id = #{item.id}
