mapper.select:
select ,tc.name company_name,tr.title recruitment_title from t_enrollment te left join t_company tc on tc.id=te.company_id
left join t_recruitment tr on tr.id = te.recruitment_id 

	 
	
		and te.recruitment_id = #{obj.recruitment.id}
	
	
		and te.company_id = #{obj.company.id}
	
	
		tc.name like concat('%',#{obj.company.name},'%')

select conut(1) from t_enrollment te left join t_company tc on tc.id=te.compnayId
left join t_recruitment tr on tr.id = te.recruitment_id 

	te.data_state = 1 
	
		and te.recruitment_id = #{obj.recruitmentId}
	
	
		and te.company_id = #{obj.companyId}

SELECT count(1) from t_enrollment te 
left join t_company tc on tc.id=te.company_id
left join t_recruitment tr on tr.id = te.recruitment_id 
left join t_user tu on tu.id = te.user_id

	te.data_state = 1 
	
		and te.state = #{obj.state}
	
	
		and tu.name = #{obj.user.name}
	
	
		and tu.mobile = #{obj.user.mobile}
	
	
		and tc.name like concat('%',#{obj.company.name},'%')
	
	
		and te.create_time >= #{obj.enrollTimeStart}
	
	
		and #{obj.enrollTimeEnd} >= te.create_time

SELECT , tc.name company_name, tr.title recruitment_title, tu.name as user_name, tu.mobile as user_mobile from t_enrollment te left join t_company tc on tc.id=te.company_id left join t_recruitment tr on tr.id = te.recruitment_id left join t_user tu on tu.id=te.user_id
te.data_state = 1
and te.state = #{obj.state}
and tu.name = #{obj.user.name}
and tu.mobile = #{obj.user.mobile}
and tc.name like concat('%',#{obj.company.name},'%')
and te.create_time >= #{obj.enrollTimeStart}
and #{obj.enrollTimeEnd} >= te.create_time
order by te.create_time desc

update.foreach:
update t_enrollment
data_state=#{item.dataState}, update_time = now()
where id = #{item.id}

mapper.update:
update t_enrollment 
set 
	update_TIME=now(),
	is_talked=#{obj.isTalked},
	talk_result=#{obj.talkResult}
