mapper.select:
select

from t_permission_relation 

    and data_state=1

        AND permission_id = #{obj.permissionId}


        and parent_permission_id = #{obj.parentPermissionId}


        and data_state = #{obj.dataState}

update.foreach:
update t_permission_relation 

    data_state = 0,
    update_time = #{item.updateTime}

where id = #{item.id}

mapper.update:
        update t_permission_relation set
		permission_id = #{obj.permissionId},
		parent_permission_id = #{obj.parentPermissionId},
		data_state = #{obj.dataState},
		update_time = #{obj.updateTime}
		where id = #{id}

update t_permission_relation 
set data_state = 0,
update_time = #{obj.updateTime}
where id = #{id}
